import { Component } from '@angular/core';

@Component({
  selector: 'app-barra-menu',
  templateUrl: './barra-menu.component.html',
  styleUrls: ['./barra-menu.component.css']
})
export class BarraMenuComponent {

}

<div style="display: flex; justify-content: center;">
  <div style="position: fixed; top: 0; width: 80%; height: 60px; background-color: red; display: flex; justify-content: space-between; align-items: center; padding: 0 16px; box-sizing: border-box;">
    <h1 style="margin: 0; color: rgb(234, 210, 210); font-size: 24px;">EL VALIDAATORR</h1>
    <button style="background-color: rgb(255, 255, 255); color: red; border: none; padding: 8px 16px; border-radius: 4px; cursor: pointer; font-size: 16px;">Click me</button>
  </div>
  <div style="height: 200vh;"></div>
  <script>
    window.addEventListener("scroll", function() {
      if (window.pageYOffset > 0) {
        document.body.style.backgroundColor = "rgba(255, 0, 0, 0.1)";
      } else {
        document.body.style.backgroundColor = "white";
      }
    });
  </script> 

</div>
